mapper.select:
select
    count(*) > 0
from account
where
    email = #{email}
    and register_status = ${@red.semipro.domain.enums.RegisterStatus@REGULAR.value}

select
    count(*) > 0
from account
where
    username = #{username}
    and register_status = ${@red.semipro.domain.enums.RegisterStatus@REGULAR.value}
