mapper.insert:
INSERT INTO identification (
    seminar_id
    , business_type
    , ip
) VALUES (
    #{seminarId}
    , #{businessType.value}
    , #{ip}
)

mapper.update:
UPDATE identification SET
    business_type = #{businessType.value}
    , ip = #{ip}
    , updated_at = CURRENT_TIMESTAMP
WHERE
    id = #{id}
