mapper.insert:
INSERT INTO seminar_entry (
    seminar_id
    , account_id
    , seminar_ticket_id
    , stripe_customer_card_id
) VALUES (
    #{seminarId}
    , #{accountId}
    , #{seminarTicketId}
    , #{stripeCustomerCardId}
)

mapper.select:
SELECT
    COUNT(id) > 0
FROM seminar_entry
WHERE
seminar_id = #{seminarId}
AND account_id = #{accountId}
