mapper.insert:
insert into seminar ( provider_id, provider_seminar_id, seminar_type, title, summary, contents, image_path, starting_at, ending_at, opening_at, entry_starting_at, entry_ending_at, cancel_at, capacity, provider_seminar_url, reference_url, cancel_policy, prefecture_id, address, place, lat, lng, accepted, waiting, embed_code, minimum_number_hosts, created_at, updated_at ) values ( #{providerId.value}, #{providerSeminarId}, #{seminarType}, #{title}, #{summary}, #{contents}, #{imagePath}, #{startingAt}, #{endingAt}, #{openingAt}, #{entryStartingAt}, #{entryEndingAt}, #{cancelAt}, #{capacity}, #{providerSeminarUrl}, #{referenceUrl}, #{cancelPolicy}, #{prefecture.id}, #{address}, #{place}, #{lat}, #{lng}, #{accepted}, #{waiting}, #{embedCode}, #{minimumNumberHosts}, current_timestamp,
#{updatedAt}
current_timestamp
)

mapper.update:
update seminar set seminar_type = #{seminarType}, title = #{title}, summary = #{summary}, contents = #{contents}, image_path = #{imagePath}, starting_at = #{startingAt}, ending_at = #{endingAt}, opening_at = #{openingAt}, entry_starting_at = #{entryStartingAt}, entry_ending_at = #{entryEndingAt}, cancel_at = #{cancelAt}, capacity = #{capacity}, provider_seminar_url = #{providerSeminarUrl}, reference_url = #{referenceUrl}, cancel_policy = #{cancelPolicy}, prefecture_id = #{prefecture.id}, address = #{address}, place = #{place}, lat = #{lat}, lng = #{lng}, accepted = #{accepted}, waiting = #{waiting}, embed_code = #{embedCode}, minimum_number_hosts = #{minimumNumberHosts},
updated_at = #{updatedAt}
updated_at = current_timestamp
where id = #{id}
